import React from 'react'
import styled from 'styled-components'


export const Pagination = ({ postPerPage, totalPosts }) => {

    const pageNums = []

    for (let i = 1; i <= Math.ceil(totalPosts / postPerPage); i++) {
        pageNums.push(i)
    }

    return (
        <>
            {pageNums.map(num => (
                <PageNum key={num} > <a href="#" >{num}</a></PageNum>
            ))
            }
        </>
    )
}


const PageNumContainer=styled.ul`
`

const PageNum = styled.li`
    padding :5px;
    border :1px solid #124655
`
