import React from 'react'
import styled from 'styled-components'
export const Posts = ({ posts, loading }) => {

    if (loading) {
        return <h2>Loading...</h2>
    }

    return (
        <>
            <ListGroup>
                {posts.map(post => (
                    <li key={post.id} className="list_group__item">
                        {post.title}
                    </li>
                ))}
            </ListGroup>

        </>
    )
}

const ListGroup = styled.ul`
border : 1px solid white
`
